:0;s:8:"xml_lang";s:0:"";}i:5;a:5:{s:4:"data";s:9:"WordPress";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}i:6;a:5:{s:4:"data";s:16:"WordPress Meetup";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:4:"guid";a:1:{i:0;a:5:{s:4:"data";s:32:"http://webdevstudios.com/?p=8333";s:7:"attribs";a:1:{s:0:"";a:1:{s:11:"isPermaLink";s:5:"false";}}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:11:"description";a:1:{i:0;a:5:{s:4:"data";s:390:"This is the first year that Omaha is having a WordCamp! It is running from Saturday, August 9th &#8211; Sunday, August 10th and is being held at theMetropolitan Community College. During WordCamp Omaha, you will be able to see WDS &#8230; <a class="more-link" href="http://webdevstudios.com/2014/08/06/wordcamp-oma-what-omaha/">Continue reading <span class="meta-nav">&#8594;</span></a>";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:32:"http://purl.org/dc/elements/1.1/";a:1:{s:7:"creator";a:1:{i:0;a:5:{s:4:"data";s:13:"Melissa Hoppe";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:40:"http://purl.org/rss/1.0/modules/content/";a:1:{s:7:"encoded";a:1:{i:0;a:5:{s:4:"data";s:1764:"<p><a href="http://webdevstudios.com/wp-content/uploads/2014/08/badge-250x250-speaker.png"><img class="alignright wp-image-8354 size-full" src="http://webdevstudios.com/wp-content/uploads/2014/08/badge-250x250-speaker.png" alt="badge-250x250-speaker" width="250" height="250" /></a>This is the first year that Omaha is having a WordCamp! It is running from Saturday, August 9th &#8211; Sunday, August 10th and is being held at the<span style="color: #2b2b2b">Metropolitan Community College.</span></p>\n<p>During <a href="http://2014.omaha.wordcamp.org/">WordCamp Omaha</a>, you will be able to see WDS team member <a title="Michael Beckwith" href="http://webdevstudios.com/team/michael-beckwith/">Michael Beckwith </a>speaking for the very first time! You can see him at 2:00 PM in the User/Business track. Michael will be presenting to you about &#8212; <span style="text-decoration: underline">Your Customers and You: Advice For Quality Support</span>. Here is a short description of his talk &#8211;</p>\n<blockquote><p><span style="color: #2b2b2b">From free extensions to paid, premium, ones, if you offer a product, chances are you will be asked to support using them. Well go over tips and advice to best handle these moments as well as some tools to help streamline the process, leaving your customers and users as happy as can be.</span></p></blockquote>\n<p>You will also be able to chat with Michael throughout WordCamp Omaha, including during the Hackathon on Sunday, August 10th.</p>\n<p>Michael is excited to make new connections with people who share his love for WordPress, as well as reconnect with those he&#8217;s already met. We wish him and the organizers of WordCamp Omaha good luck as they journey through their WordCamp experiences!</p>\n";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:36:"http://wellformedweb.org/CommentAPI/";a:1:{s:10:"commentRss";a:1:{i:0;a:5:{s:4:"data";s:65:"http://webdevstudios.com/2014/08/06/wordcamp-oma-what-omaha/feed/";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}s:38:"http://purl.org/rss/1.0/modules/slash/";a:1:{s:8:"comments";a:1:{i:0;a:5:{s:4:"data";s:1:"0";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}}}}}}s:44:"http://purl.org/rss/1.0/modules/syndication/";a:2:{s:12:"updatePeriod";a:1:{i:0;a:5:{s:4:"data";s:6:"hourly";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b:0;s:8:"xml_lang";s:0:"";}}s:15:"updateFrequency";a:1:{i:0;a:5:{s:4:"data";s:1:"1";s:7:"attribs";a:0:{}s:8:"xml_base";s:0:"";s:17:"xml_base_explicit";b